const erc20 = (tokenAddress, provider) => {
 const abi = [
 'function balanceOf(address account) external view returns (uint256)',
 'function approve(address spender, uint256 amount) external returns (bool)',
 'function allowance(address owner, address spender) external view returns (uint256)',
 ];
 return new ethers.Contract(tokenAddress, abi, provider);
};

const toWei = (amount, decimals = 18) => {
 return ethers.utils.parseUnits(amount.toString(), decimals);
};

const fromWei = (amount, decimals = 18) => {
 return ethers.utils.formatUnits(amount.toString(), decimals);
};

const getTokenBalance = async (token, wallet) => {
 const tokenContract = erc20(token.address, wallet);
 const balance = await tokenContract.balanceOf(wallet.address);
 return balance;
};

const getAllowance = async (routerAddress, token, wallet) => {
 const tokenContract = erc20(token.address, wallet);

 const allowanceAmount = await tokenContract.allowance(
 wallet.address,
 routerAddress
 );

 return allowanceAmount;
};

const approve = async (routerAddress, token, wallet, gasPrice) => {
 console.log('Approving...');

 const tokenContract = erc20(token.address, wallet);

 const tx = await tokenContract.approve(
 routerAddress,
 ethers.constants.MaxUint256,
 {
 gasPrice,
 }
 );

 await tx.wait();

 console.log('Approved');
};

const getFee = async (router, chainTo, walletFrom) => {
 const functionType = 1;
 const quoteDataPayload = '0x';

 const quoteData = await router.quoteLayerZeroFee(
 chainTo.id,
 functionType,
 walletFrom.address,
 quoteDataPayload,
 {
 dstGasForCall: 0,
 dstNativeAmount: 0,
 dstNativeAddr: walletFrom.address,
 }
 );

 const fee = quoteData[0];

 return fee;
};

const getGasPrice = async (provider) => {
 const gasPrice = (await provider.getGasPrice()).mul(150).div(100);
 return gasPrice;
};

const getMinAmount = async (amount) => {
 const minAmount = amount.div(1000).mul(995); // min amount is 0.5% less than amount
 return minAmount;
};

const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const stargateTransfer = async (
 tokenFromName,
 tokenToName,
 chainFromName,
 chainToName,
 amount,
 privateKey
) => {
 const chainFrom = chain[chainFromName];
 const chainTo = chain[chainToName];
 const tokenFrom = chainFrom.token[tokenFromName];
 const tokenTo = chainTo.token[tokenToName];

 if (!chainFrom || !chainTo) {
 throw new Error('Chain not supported');
 }

 if (!tokenFrom || !tokenTo) {
 throw new Error('Token not supported');
 }

 console.log(
 `Transfering ${amount} ${chainFromName}-${tokenFromName} to ${chainToName}-${tokenToName}`
 );

 const providerFrom = new ethers.providers.JsonRpcProvider(chainFrom.rpcUrl);
 const providerTo = new ethers.providers.JsonRpcProvider(chainTo.rpcUrl);

 const walletFrom = new ethers.Wallet(privateKey, providerFrom);
 const walletTo = new ethers.Wallet(privateKey, providerTo);

 const walletFromBalance = await walletFrom.getBalance();

 const balanceFrom = await getTokenBalance(tokenFrom, walletFrom);

 const amountInWei = toWei(amount, tokenFrom.decimals);

 const router = new ethers.Contract(
 chainFrom.routerAddress,
 routerABI,
 walletFrom
 );

 if (balanceFrom.lt(amountInWei)) {
 throw new Error('Insufficient balance');
 }

 const fee = await getFee(router, chainTo, walletFrom);

 const safeFee = fee.mul(2);

 if (walletFromBalance.lt(safeFee)) {
 throw new Error('Insufficient ETH balance for transaction fee');
 }

 console.log(`Transaction fee: ${fromWei(fee)}`);

 const chainToAndTokenTo = `${chainToName}-${tokenToName}`;

 const path = tokenFrom.paths.find((path) => path.includes(chainToAndTokenTo));

 if (!path) {
 throw new Error('Invalid token pair');
 }

 const allowance = await getAllowance(router.address, tokenFrom, walletFrom);

 const gasPrice = await getGasPrice(providerFrom);

 if (allowance.lt(amountInWei)) {
 await approve(router.address, tokenFrom, walletFrom, gasPrice);
 }

 const minAmount = await getMinAmount(amountInWei);

 const balanceToBefore = await getTokenBalance(tokenTo, walletTo);

 const newGasPrice = await getGasPrice(providerFrom);

 const tx = await router.swap(
 chainTo.id,
 tokenFrom.id,
 tokenTo.id,
 walletFrom.address,
 amountInWei,
 minAmount,
 { dstGasForCall: 0, dstNativeAmount: 0, dstNativeAddr: walletFrom.address },
 walletFrom.address,
 '0x',
 { value: fee, gasPrice: newGasPrice }
 );

 await tx.wait();

 let balanceToAfter = await getTokenBalance(tokenTo, walletTo);

 cliSpinner.start();

 while (balanceToAfter.eq(balanceToBefore)) {
 await sleep(1000);
 balanceToAfter = await getTokenBalance(tokenTo, walletTo);
 }

 cliSpinner.stop();

 console.log(`Success! Transaction hash: ${tx.hash}`);
};

const stargateTransferEth = async (
 chainFromName,
 chainToName,
 amount,
 privateKey
) => {
 const chainFrom = chain[chainFromName];
 const chainTo = chain[chainToName];
 const validChains = ['ethereum', 'arbitrum', 'optimism'];

 if (
 !validChains.includes(chainFromName) ||
 !validChains.includes(chainToName)
 ) {
 throw new Error('Chain not supported');
 }

 if (chainFromName === chainToName) {
 throw new Error('Invalid chain pair');
 }

 console.log(
 `Transfering ${amount} ETH from ${chainFromName} to ${chainToName}`
 );

 const provider = new ethers.providers.JsonRpcProvider(chainFrom.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const balance = await wallet.getBalance();

 console.log('Balance of ETH:', fromWei(balance));

 const amountInWei = toWei(amount);

 const router = new ethers.Contract(
 chainFrom.routerAddress,
 routerABI,
 wallet
 );

 const fee = await getFee(router, chainTo, wallet);
 const fullAmount = amountInWei.add(fee);
 const safeAmount = amountInWei.add(fee.mul(2));

 if (balance.lt(safeAmount)) {
 throw new Error(`Insufficient balance`);
 }

 console.log(`Transaction fee: ${fromWei(fee)}`);

 const ethRouter = new ethers.Contract(
 chainFrom.ethRouterAddress,
 ethRouterABI,
 wallet
 );

 const balanceToBefore = await wallet.getBalance();

 const minAmount = await getMinAmount(amountInWei);

 const gasPrice = await getGasPrice(provider);

 const tx = await ethRouter.swapETH(
 chainTo.id,
 wallet.address,
 wallet.address,
 amountInWei,
 minAmount,
 { value: fullAmount, gasPrice: gasPrice }
 );

 await tx.wait();

 let balanceToAfter = await wallet.getBalance();

 cliSpinner.start();

 while (balanceToAfter.eq(balanceToBefore)) {
 await sleep(1000);
 balanceToAfter = await wallet.getBalance();
 }

 cliSpinner.stop();

 console.log(`Success! Transaction hash: ${tx.hash}`);
};

