const erc20 = (tokenAddress, provider) => {
 const abi = [
 'function balanceOf(address account) external view returns (uint256)',
 'function approve(address spender, uint256 amount) external returns (bool)',
 'function allowance(address owner, address spender) external view returns (uint256)',
 ];
 return new ethers.Contract(tokenAddress, abi, provider);
};

const getAllowance = async (routerAddress, tokenAddress, wallet) => {
 const tokenContract = erc20(tokenAddress, wallet);

 const allowanceAmount = await tokenContract.allowance(
 wallet.address,
 routerAddress
 );

 return allowanceAmount;
};

const approve = async (routerAddress, tokenAddress, wallet, gasPrice) => {
 console.log('Approving...');

 const tokenContract = erc20(tokenAddress, wallet);

 const tx = await tokenContract.approve(
 routerAddress,
 ethers.constants.MaxUint256,
 {
 gasPrice,
 }
 );

 await tx.wait();

 console.log('Approved');
};

const getTokenBalance = async (tokenAddress, wallet) => {
 const tokenContract = erc20(tokenAddress, wallet);
 const balance = await tokenContract.balanceOf(wallet.address);
 return balance;
};

const toWei = (amount, decimals = 18) => {
 return ethers.utils.parseUnits(amount.toString(), decimals);
};

const fromWei = (amount, decimals = 18) => {
 return ethers.utils.formatUnits(amount.toString(), decimals);
};

const getGasPrice = async (provider) => {
 const gasPrice = (await provider.getGasPrice()).mul(150).div(100);
 return gasPrice;
};

const addLiquidity = async (tokenName, chainName, amount, privateKey) => {
 const chain = chainData[chainName];
 const token = tokenData[chainName][tokenName];

 if (!chain) {
 throw new Error('Chain not supported');
 }

 if (!token) {
 throw new Error('Token not supported');
 }

 if (token === 'eth') {
 throw new Error('ETH is not supported');
 }

 console.log(`Adding liquidity to ${chainName}-${tokenName} pool`);

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const router = new ethers.Contract(chain.routerAddress, routerABI, wallet);

 const balanceInWei = await getTokenBalance(token.address, wallet);
 const balance = fromWei(balanceInWei, token.decimals);

 const amountInWei = toWei(amount, token.decimals);

 if (balanceInWei.lt(amountInWei)) {
 throw new Error('Insufficient balance');
 }

 console.log(`Your balance on ${chainName}-${tokenName}: ${balance}`);

 const allowance = await getAllowance(router.address, token.address, wallet);

 const gasPrice = await getGasPrice(provider);

 if (allowance.lt(amountInWei)) {
 await approve(router.address, token.address, wallet, gasPrice);
 }

 const newGasPrice = await getGasPrice(provider);

 const tx = await router.addLiquidity(token.id, amountInWei, wallet.address, {
 gasPrice: newGasPrice,
 });

 await tx.wait();

 console.log(`Success! Transaction hash: ${tx.hash}`);
};

const removeLiquidity = async (tokenName, chainName, privateKey) => {
 const chain = chainData[chainName];
 const token = tokenData[chainName][tokenName];

 if (!chain) {
 throw new Error('Chain not supported');
 }

 if (!token) {
 throw new Error('Token not supported');
 }

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const router = new ethers.Contract(chain.routerAddress, routerABI, wallet);

 const amountInWei = await getTokenBalance(token.lpAddress, wallet);
 const amount = fromWei(amountInWei, token.decimals);

 console.log(`Removing ${amount} LP from ${chainName}-${tokenName}`);

 const allowance = await getAllowance(router.address, token.lpAddress, wallet);

 const gasPrice = await getGasPrice(provider);

 if (allowance.lt(amountInWei)) {
 await approve(router.address, token.lpAddress, wallet, gasPrice);
 }

 const tx = await router.instantRedeemLocal(
 token.id,
 amountInWei,
 wallet.address,
 {
 gasPrice,
 }
 );

 await tx.wait();

 console.log(`Success! Transaction hash: ${tx.hash}`);
};

const addLiquidityETH = async (chainName, amount, privateKey) => {
 const validChains = ['ethereum', 'arbitrum', 'optimism'];

 if (!validChains.includes(chainName)) {
 throw new Error('Chain not supported');
 }

 console.log(`Adding ${amount} ETH liquidity to ${chainName}-eth pool`);

 const chain = chainData[chainName];

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const ethRouter = new ethers.Contract(
 chain.ethRouterAddress,
 ethRouterABI,
 wallet
 );

 const balanceInWei = await wallet.getBalance();

 const balance = fromWei(balanceInWei);

 const amountInWei = toWei(amount);

 if (balanceInWei.lt(amountInWei)) {
 throw new Error('Insufficient balance');
 }

 console.log(`Your balance on ${chainName}-eth: ${balance}`);

 const tx = await ethRouter.addLiquidityETH({
 value: amountInWei,
 });

 await tx.wait();

 console.log(`Success! Transaction hash: ${tx.hash}`);
};

const getLPTokenBalance = async (tokenName, chainName, privateKey) => {
 const chain = chainData[chainName];
 const token = tokenData[chainName][tokenName];

 if (!chain) {
 throw new Error('Chain not supported');
 }

 if (!token) {
 throw new Error('Token not supported');
 }

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const lptBalanceInWei = await getTokenBalance(token.lpAddress, wallet);
 const lptBalance = fromWei(lptBalanceInWei, token.decimals);

 console.log(`Your LP ${tokenName} balance is: ${lptBalance}`);
};

