const erc20 = (tokenAddress, provider) => {
 const abi = [
 'function balanceOf(address account) external view returns (uint256)',
 'function approve(address spender, uint256 amount) external returns (bool)',
 'function allowance(address owner, address spender) external view returns (uint256)',
 ];
 return new ethers.Contract(tokenAddress, abi, provider);
};

const getAllowance = async (routerAddress, tokenAddress, wallet) => {
 const tokenContract = erc20(tokenAddress, wallet);

 const allowanceAmount = await tokenContract.allowance(
 wallet.address,
 routerAddress
 );

 return allowanceAmount;
};

const approve = async (routerAddress, tokenAddress, wallet, gasPrice) => {
 console.log('Approving...');

 const tokenContract = erc20(tokenAddress, wallet);

 const tx = await tokenContract.approve(
 routerAddress,
 ethers.constants.MaxUint256,
 {
 gasPrice,
 }
 );

 await tx.wait();

 console.log('Approved');
};

const getTokenBalance = async (tokenAddress, wallet) => {
 const tokenContract = erc20(tokenAddress, wallet);
 const balance = await tokenContract.balanceOf(wallet.address);
 return balance;
};

const toWei = (amount, decimals = 18) => {
 return ethers.utils.parseUnits(amount.toString(), decimals);
};

const fromWei = (amount, decimals = 18) => {
 return ethers.utils.formatUnits(amount.toString(), decimals);
};

const getGasPrice = async (provider) => {
 const gasPrice = (await provider.getGasPrice()).mul(150).div(100);
 return gasPrice;
};

const stakeSTG = async (chainName, amount, unlockInMonths, privateKey) => {
 const chain = chainData[chainName];

 if (!chain) {
 throw new Error('Chain not supported');
 }

 console.log(`Staking ${amount} STG on ${chainName}`);

 const { votingEscrowAddress, stargateTokenAddress } = chain;

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const amountInWei = toWei(amount);

 const votingEscrow = new ethers.Contract(
 votingEscrowAddress,
 votingEscrowABI,
 wallet
 );

 const stargateTokenBalanceInWei = await getTokenBalance(
 stargateTokenAddress,
 wallet
 );

 const stargateTokenBalance = fromWei(stargateTokenBalanceInWei);

 if (stargateTokenBalanceInWei.lt(amountInWei)) {
 throw new Error('Insufficient balance');
 }

 console.log(`Your balance: ${stargateTokenBalance} STG`);

 const allowanceAmountInWei = await getAllowance(
 votingEscrowAddress,
 stargateTokenAddress,
 wallet
 );

 const gasPrice = await getGasPrice(provider);

 if (allowanceAmountInWei.lt(amountInWei)) {
 await approve(votingEscrowAddress, stargateTokenAddress, wallet, gasPrice);
 }

 const now = Date.now() / 1000; // in seconds

 const secondsInMonth = 60 * 60 * 24 * 30;

 const unlockTimeRounded = Math.ceil(now + secondsInMonth * unlockInMonths);

 const unlockTimeRoundedSafe = unlockTimeRounded + 1000;

 const newGasPrice = await getGasPrice(provider);

 const tx = await votingEscrow.create_lock(
 amountInWei,
 unlockTimeRoundedSafe,
 {
 gasPrice: newGasPrice,
 }
 );

 await tx.wait();

 console.log(`Staked ${amount} for ${unlockInMonths} months`);
 console.log(`Transaction hash: ${tx.hash}`);
};

const unstakeSTG = async (chainName, privateKey) => {
 const chain = chainData[chainName];

 if (!chain) {
 throw new Error('Chain not supported');
 }

 console.log(`Unstaking STG on ${chainName}`);

 const { votingEscrowAddress } = chain;

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);

 const wallet = new ethers.Wallet(privateKey, provider);

 const votingEscrow = new ethers.Contract(
 votingEscrowAddress,
 votingEscrowABI,
 wallet
 );

 const gasPrice = await getGasPrice(provider);

 const tx = await votingEscrow.withdraw({ gasPrice });

 await tx.wait();

 console.log('Unstaked STG successfully');
 console.log(`Transaction hash: ${tx.hash}`);
};

/*
stakeSTG(
 'arbitrum', - chain name (ethereum, bnb, avalanche, polygon, arbitrum, fantom)
 '0.121', - amount STG to stake
 '3', - unlock time in months (from 1 to 34)
 'your private key'
)

unstakeSTG(
 'arbitrum', - chain name (ethereum, bnb, avalanche, polygon, arbitrum, fantom)
 'your private key'
)
*/


