const getTokenBalance = async (tokenName, chainName, privateKey) => {

 const chain = chainData[chainName];

 if (!chain) {
 throw new Error('Invalid chain name');
 }

 if (tokenName === chain.native) {
 return await _getNativeBalance(chainName, privateKey);
 }

 const token = tokenData[chainName][tokenName];

 if (!token) {
 throw new Error('Invalid token name');
 }

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);
 const wallet = new ethers.Wallet(privateKey, provider);

 const erc20 = createERC20(token.address, wallet);
 const balanceInWei = await erc20.balanceOf(wallet.address);
 const balance = ethers.utils.formatUnits(balanceInWei, token.decimals);

 if (balanceInWei.eq(0)) {
 return '0';
 }

 return balance;
