async function checkRpcUrls(rpcUrls) {
 const results = [];

 for (const url of rpcUrls) {
 const provider = new providers.JsonRpcProvider(url);

 try {
 const blockNumber = await provider.getBlockNumber();
 results.push({ url, success: true, blockNumber });
 } catch (error) {
 const err = new Error(`RPC НЕ РАБОТАЕТ ${url}`);
 results.push({ url, success: false, error: err });
 throw err;
 }
 }

 return results;
}

