 while (!functionName && attemptsLeft > 0) {
 try {
 const queryParams = `?module=account&action=txlist&address=${address}&startblock=0&endblock=latest&sort=desc`;
 const url = new URL(queryParams, chain.apiUrl);
 const response = await fetch(url);
 const { result } = await response.json();
 const latestTransaction = result.at(0);
 functionName = latestTransaction.functionName;
 } catch (e) {
 await wait(timeToSleep);
 attemptsLeft -= 1;
 timeToSleep *= 1.4;
 }
 }

 if (!functionName) {
 throw new Error('Unable to fetch latest transaction');
 }

 return functionName;
};

