const createERC20 = (tokenAddress, provider) => {
 const abi = [
 'function balanceOf(address account) external view returns (uint256)',
 'function approve(address spender, uint256 amount) external returns (bool)',
 'function allowance(address owner, address spender) external view returns (uint256)',
 ];
 return new ethers.Contract(tokenAddress, abi, provider);
};


const _getNativeBalance = async (chainName, privateKey) => {
 const chain = chainData[chainName];
 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);
 const wallet = new ethers.Wallet(privateKey, provider);

 const balanceInWei = await wallet.getBalance();
 const balance = ethers.utils.formatEther(balanceInWei);

 if (balanceInWei.eq(0)) {
 return '0';
 }

 return balance;
};

const getTokenBalance = async (tokenName, chainName, privateKey) => {

 const chain = chainData[chainName];

 if (!chain) {
 throw new Error('Invalid chain name');
 }

 if (tokenName === chain.native) {
 return await _getNativeBalance(chainName, privateKey);
 }

 const token = tokenData[chainName][tokenName];

 if (!token) {
 throw new Error('Invalid token name');
 }

 const provider = new ethers.providers.JsonRpcProvider(chain.rpcUrl);
 const wallet = new ethers.Wallet(privateKey, provider);

 const erc20 = createERC20(token.address, wallet);
 const balanceInWei = await erc20.balanceOf(wallet.address);
 const balance = ethers.utils.formatUnits(balanceInWei, token.decimals);

 if (balanceInWei.eq(0)) {
 return '0';
 }

 return balance;
};
if ([[TYPE]] == 'Before') {

 if ([[TASK_NETWORK_OUT]].toLowerCase().trim() == "apt") {
 [[BALANCE_BEFORE]] = await getTokenBalance([[TASK_COIN_OUT]].toLowerCase(),[[TASK_NETWORK_OUT_S]],[[PRIVATE_KEY_APT]])

 } else {
 [[BALANCE_BEFORE]] = await getTokenBalance([[TASK_COIN_OUT]].toLowerCase(),[[TASK_NETWORK_OUT_S]],[[PRIVATE_KEY]])

 }
} else {

 if ([[TASK_NETWORK_OUT]].toLowerCase().trim() == "apt") {
 [[BALANCE_AFTER]] = await getTokenBalance([[TASK_COIN_OUT]].toLowerCase(),[[TASK_NETWORK_OUT_S]],[[PRIVATE_KEY_APT]])

 } else {
 [[BALANCE_AFTER]] = await getTokenBalance([[TASK_COIN_OUT]].toLowerCase(),[[TASK_NETWORK_OUT_S]],[[PRIVATE_KEY]])

}

